$(".icon-searchme").click(function(){
		$(".sb-search-input").css({
			"width": "-webkit-fill-available",
			"visibility": "visible"
		});
		$(".sb-search-submit").css({
			"z-index": 9999
		});
	});
	
	$(document).click( function(event){
		if( $(event.target).closest(".sb-search-input").length || $(event.target).closest(".icon-searchme").length) {
			return;
		} 
		$(".sb-search-input").css({
			"width": "0",
			"visibility": "hidden"
		});
		$(".sb-search-submit").css({
			"z-index": -1
		});
			
	
		//event.stopPropagation();
	});